              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (1,1,1,   'Home', '<p><div style="background-color: #f90; padding: 5px"> </p> <p><a href="http://www.aida-americas.org/email/updates/2008_12.html">AIDA Monthly Update: Thanks for your support! AIDA made great progress in 2008! (December 2008)</a></div></p> ','', '', NOW(), 1),
              (1001,1001,1,'Inicio', '<p> <br></p> <p><table width="100%" border="0" cellpadding="5" cellspacing="0" bgcolor="#FF9900"><tr><td><a href="http://www.aida-americas.org/email/updates/2008_12.html">Boletín de AIDA: ¡Gracias por su apoyo! ¡AIDA logró grandes avances en 2008! (Diciembre de 2008)</a></td></tr></table></p> ', '', '', NOW(), 1);
