            (2,     2,     'page','en',    'About AIDA',1,   1,      NOW(),   NOW(), 0,0,0,0,2,0),
            (1002,1002,'page','es',    'Acerca de AIDA',1,   1,      NOW(),   NOW(), 0,0,0,0,2,0);
