INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (9,9,1,   'Contact', '<p><HR WIDTH="75%" COLOR="#0000CD" SIZE="2"> </p> <p><center><font color="#0000CD">Are you interested in hearing about the latest developments in AIDA’s work? </p> <p><a href="/?q=subscribe"><b><font color="#0000CD">Sign up now for our monthly updates</font></b></a> and stay informed!</font></center> </p> <p><HR WIDTH="75%" COLOR="#0000CD" SIZE="2"> </p> <p>You may contact AIDA at the addresses below, or through any of our <a href="/?q=partorgs">participating organizations</a>. </p> <p><html> </p> <p><body> </p> <p><table border="0" width="100%" cellpadding="10"><tr><td width="50%" valign="top"> </p> <p><b>U.S. Address</b> </p> <p>AIDA c/o Earthjustice <br />426 17<sup>th</sup> Street, 6<sup>th</sup> Floor <br />Oakland, CA 94612 <br />USA </p> <p><b>Telephone</b>: (510) 550-6753 </p> <p><b>Fax</b>: (510) 550-6740 </p> <p><b>Email</b>: <h>aida@aida-americas.org</h></td><td width="50%" valign="top"> </p> <p><b>Mexico Address</b> </p> <p>AIDA c/o CEMDA <br />Atlixco 138, Colonia Condesa <br />Mexico, DF 06140 <br />Mexico </p> <p><b>Telephone/Fax</b>: 52-55 52120141 </p> <p><b>Email</b>: <h>aida@aida-americas.org</h></td></tr></table></body> </p> <p></html></p> ','', '', NOW(), 1),
              (1009,1009,1,'Contáctenos', '<p><HR WIDTH="85%" COLOR="#0000CD" SIZE="2"> </p> <p><center><font color="#0000CD">Tiene interés en saber más sobre los más recientes progresos en el trabajo de AIDA? </p> <p><a href="/?q=subscribe"><b><font color="#0000CD">Suscríbase ahora a nuestras actualizaciones mensuales</font></b></a> y manténgase informado! </font></center> </p> <p><HR WIDTH="85%" COLOR="#0000CD" SIZE="2"> </p> <p>Puede comunicarse con AIDA en la siguiente dirección o a través de cualquiera de las <a href="/?q=partorgs">organizaciones participantes</a>. </p> <p><html> </p> <p><body> </p> <p><table border="0" width="100%" cellpadding="10"><tr><td width="50%" valign="top"> </p> <p><b>Dirección en los Estados Unidos</b> </p> <p>AIDA c/o Earthjustice <br />426 17<sup>th</sup> Street, 6<sup>th</sup> Floor <br />Oakland, CA 94612 <br />USA </p> <p><b>Telephone</b>: (510) 550-6753 </p> <p><b>Fax</b>: (510) 550-6740 </p> <p><b>Correo electrónico</b>: <h>aida@aida-americas.org</h></td><td width="50%" valign="top"> </p> <p><b>Dirección de México</b> </p> <p>AIDA c/o CEMDA <br />Atlixco 138, Colonia Condesa <br />Mexico, DF 06140 <br />Mexico </p> <p><b>Telephone/Fax</b>: 52-55 52120141 </p> <p><b>Correo electrónico</b>: <h>aida@aida-americas.org</h></td></tr></table></body> </p> <p></html></p> ', '', '', NOW(), 1);
