INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (10,10,1,   'Donate to AIDA', '<p>AIDA depends on donations from friends and supporters to continue our fight for environmental protection and justice in the hemisphere. You can help by <a href="http://www.guidestar.org/partners/networkforgood/donate.jsp?ein=94-3292116"><b>donating online</b></a>, or by printing out a {DOC Donate.pdf <b>donation form</b>}</a> and mailing your payment. </p> <p><center><font color="#0000CD">Are you interested in hearing about the latest developments in AIDA’s work? <br /><a href="/?q=subscribe"><b><font color="#0000CD">Sign up now for our monthly updates</font></b></a> and stay informed!</font></center> </p> <p><a href="/?q=aidafinances"><b>AIDA Finances</b></a></p> ','', '', NOW(), 1),
              (1010,1010,1,'Apoye a AIDA', '<p>AIDA depende de donaciones de amigos y colaboradores para continuar nuestra lucha por la protección y la justicia ambiental en el hemisferio. Puede ayudar <a href="http://www.guidestar.org/partners/networkforgood/donate.jsp?ein=94-3292116"><b>donando en línea</b></a>, o imprimiendo y enviando por correo nuestro {DOC Donar.pdf <b>formato de donaciones</b>}</a>. </p> <p><center><font color="#0000CD">Tiene interés en saber más sobre los más recientes progresos en el trabajo de AIDA? <br /><a href="/?q=subscribe"><b><font color="#0000CD">Suscríbase ahora a nuestras actualizaciones mensuales</font></b></a> y manténgase informado! </font></center> </p> <p><a href="/?q=aidafinances"><b>Finanzas de AIDA</b></a></p> ', '', '', NOW(), 1);
