INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (11,11,1,   'Participating Organizations', '<p><table cellspacing="3" cellpadding="4" class="orgs"><tr><th class="country" width="30%"> </p> <p><b>Country</b> </p> <p></th> </p> <p><th class="org"> </p> <p><b>Organization(s)</b> </p> <p></th></tr><tr><td class="country"> </p> <p>Argentina</td><td class="org"> </p> <p><a href="http://www.cedha.org.ar/en/"><b>CEDHA</b> (Centro de Derechos Humanos y Ambiente, Center for Human Rights and Environment) </p> <p></a></td></tr><tr><td class="country"> </p> <p>Canada</td><td class="org"> </p> <p><a href="http://www.sierralegal.org/"><b>Ecojustice</b> </p> <p></a></td></tr><tr><td class="country"> </p> <p>Chile</td><td class="org"> </p> <p><a href="http://www.fima.cl"><b>FIMA</b> (Fiscalía del Medio Ambiente) </p> <p></a></td></tr><tr><td class="country" rowspan="2"> </p> <p>Costa Rica</td><td class="org"> </p> <p><a href="http://www.cedarena.org/"><b>CEDARENA</b> (Centro de Derecho Ambiental y de los Recursos Naturales, The Environmental and Natural Resources Law Center) </p> <p></a></td></tr><tr><td class="org"> </p> <p><a href="/?q=jpn"><b>JPN</b> (Justicia para la Naturaleza, Justice for Nature)</a></td></tr><tr><td class="country"> </p> <p>Ecuador</td><td class="org"> </p> <p><a href="http://www.ecolex-ec.org/"><b>ECOLEX</b> (Corporación de Gestión y Derecho Ambiental, Organization for Environmental Law and Management) </p> <p></a></td></tr><tr><td class="country"> </p> <p>Mexico</td><td class="org"> </p> <p><a href="http://www.cemda.org.mx/"><b>CEMDA</b> (Centro Mexicano de Derecho Ambiental, The Mexican Environmental Law Center) </p> <p></a></td></tr><tr><td class="country"> </p> <p>Peru</td><td class="org"> </p> <p><a href="http://www.spda.org.pe/"><b>SPDA</b> (Sociedad Peruana de Derecho Ambiental, The Peruvian Society for Environmental Law) </p> <p></a></td></tr><tr><td class="country"> </p> <p>United States</td><td class="org"> </p> <p><a href="http://www.earthjustice.org/"><b>Earthjustice</b> </p> <p></a></td></tr></table></p> ','', '', NOW(), 1),
              (1011,1011,1,'Organizaciones Participantes', '<p><table cellspacing="3" cellpadding="4" class="orgs"><tr><th class="country" width="30%"> </p> <p><b>País</b> </p> <p></th> </p> <p><th class="org"> </p> <p><b>Organización</b> </p> <p> </th></tr><tr><td class="country"> </p> <p> Argentina</td><td class="org"> </p> <p> <a href="http://www.cedha.org.ar/es/"><b>CEDHA</b> (Centro de Derechos Humanos y Ambiente, Center for Human Rights and Environment)</a></td></tr><tr><td class="country"> </p> <p> Canad&aacute;</td><td class="org"> </p> <p> <a href="http://www.sierralegal.org/"><b>Ecojustice</b></a></td></tr><tr><td class="country"> </p> <p> Chile</td><td class="org"> </p> <p><a href="http://www.fima.cl"><b>FIMA</b> (Fiscalía del Medio Ambiente)</a></tr><tr><td class="country" rowspan="2"> </p> <p> Costa Rica</td><td class="org"> </p> <p> <a href="http://www.cedarena.org/"><b>CEDARENA</b> (Centro de Derecho Ambiental y de los Recursos Naturales, The Environmental and Natural Resources Law Center)</a></td></tr><tr><td class="org"> </p> <p> <a href="partorgs/JPN-CResp.php"><b>JPN</b> (Justicia para la Naturaleza,Justice for Nature)</a></td></tr><tr><td class="country"> </p> <p> Ecuador</td><td class="org"> </p> <p> <a href="http://www.ecolex-ec.org/"><b>ECOLEX</b> (Corporación de Gestión y Derecho Ambiental, Organization for Environmental Law Management)</a></td></tr><tr><td class="country"> </p> <p> México</td><td class="org"> </p> <p> <a href="http://www.cemda.org.mx/"><b>CEMDA</b> (Centro Mexicano de Derecho Ambiental, The Mexican Environmental Law Center)</a></td></tr><tr><td class="country"> </p> <p> Perú</td><td class="org"> </p> <p> <a href="http://www.spda.org.pe/"><b>SPDA</b> (Sociedad Peruana de Derecho Ambiental, The Peruvian Society for Environmental Law)</a></td></tr><tr><td class="country"> </p> <p> Estados Unidos</td><td class="org"> </p> <p> <a href="http://www.earthjustice.org/"><b>Earthjustice</b></a></td></tr></table></p> ', '', '', NOW(), 1);
