INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (76,76,1,   'Page not found', '<p>The page you are trying to visit does not exist. Either there was some mistake with the referring page, or you are trying to reference an out-of-date page. </p> <p>Please hit the "BACK" button and access a different page. </p> <p>Please let us know if you think our site is broken. </p> <p>Thanks.</p> ','', '', NOW(), 1),
              (1076,1076,1,'Pagina no existe', '<p>La página que está tratando de visitar no existe. Hubo un error en la página que lo envió o está tratando de llegar a una referencia que no está actualizada. </p> <p>Por favor oprima la tecla “ATRAS” para regresar a la página anterior. </p> <p>Comuníquese con nosotros si cree que nuestro website no funciona. </p> <p>Gracias.</p> ', '', '', NOW(), 1);
