INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (82,82,1,   'Useful Links', '<p>For more information please visit the following sites: <li><a href="http://www.amazonalliance.org/" target="_top">Amazon Alliance <li><a href="http://www.plancolombia.gov.co/index.asp" target="_top">Colombian Presidential Website for Plan Colombia <li><a href="http://www.defensoria.org.co" target="_top">Ombudsman Office (Defensoría del Pueblo) <li><a href="http://www.lawg.org/" target="_top">Latin American Working Group <li><a href="http://www.mamacoca.org/" target="_top">MamaCoca <li><a href="http://www.tni.org/drugs/index.htm" target="_top">Transnational Institute <li><a href="http://www.wola.org/" target="_top">Washington Office on Latin America <li><a href="http://www.usfumigation.org/" target="_top">US Fumigation <li><a href="http://www.state.gov/p/wha/rt/plncol/" target="_top">U.S. Department of State and Plan Colombia <li><a href="http://www.witnessforpeace.org/" target="_top">Witness for Peace</p> ','', '', NOW(), 1),
              (1082,1082,1,'Enlaces Utiles', '<p>Para mayor información, puede visitar: <li><a href="http://www.amazonalliance.org/" target="_top">Amazon Alliance <li><a href="http://www.state.gov/p/wha/rt/plncol/" target="_top">Departamento de Estado de Estados Unidos <li><a href="http://www.lawg.org/" target="_top">Latin American Working Group <li><a href="http://www.mamacoca.org/" target="_top">MamaCoca <li><a href="http://www.plancolombia.gov.co/index.asp" target="_top">Plan Colombia, Presidencia de la República de Colombia <li><a href="http://www.tni.org/drugs/index.htm" target="_top">Transnational Institute <li><a href="http://www.usfumigation.org/" target="_top">US Fumigation <li><a href="http://www.wola.org/" target="_top">Washington Office on Latin America <li><a href="http://www.witnessforpeace.org/" target="_top">Witness for Peace</p> ', '', '', NOW(), 1);
