INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (109,109,1,   'PAMA 2004 - 2011', '<p>{DOC 0.1PAMA.pdf Fundamentos Para La Modificación Del Pama Del Complejo Metalurgico de La Oroya--} </p> <p>{DOC 0.2PAMA.pdf Indice--} </p> <p>Chapter 1 {DOC 1PAMA.pdf Resumen Ejecutivo--} </p> <p>Chapter 2 {DOC 2PAMA.pdf Antecedentes--} </p> <p>Chapter 3 {DOC 3PAMA.pdf Compromiso PAMA Actual--} </p> <p>Chapter 4 {DOC 4PAMA.pdf PAMA Para el Complejo Metalurgico de La Oroya Periodo 2004-2011--} </p> <p>Chapter 5 {DOC 5PAMA.pdf Proyectos Incluidos en el PAMA para el Complejo Metalurgico de La Oroya Periodo 2004-2011--} </p> <p> <ul></p> <p>Section 5.6 {DOC 5.6PAMA.pdf Planta de Acido Sulfurico y Sistema de Acondicionamiento de Gases--} </p> <p>Section 5.7 {DOC 5.7PAMA.pdf Reduccion de Emision de Polvo por Chimenea Principal--} </p> <p>Section 5.8 {DOC 5.8PAMA.pdf Reduccion de Emision de Polvo Generado por Emisiones Fugitivas--} </p> <p>Section 5.9 {DOC 5.9PAMA.pdf Proyectos Amientales Complementarios--} </p> <p></ul> </p> <p>6 {DOC 6PAMA.pdf Anexos Indice--} </p> <p> <ul></p> <p>6.1 {DOC 6.1PAMA.pdf Anexos I: Cronograma de Inversiones 2004-2011--} </p> <p>6.2 {DOC 6.2PAMA.pdf Anexos II: Diagramas de Gantt de los proyectos considerados en el plan ambiental 2004-2011--} </p> <p>6.3 {DOC 6.3PAMA.pdf Anexos III: Air Lead Quality Improvement Expectations--} </p> <p>6.4 {DOC 6.4PAMA.pdf Anexos IV: Relative Contributions of La Oroya Main Stack--} </p> <p>6.5 {DOC 6.5PAMA.pdf Anexos V: Analysis of La Oroya Monitoring Data--} </p> <p>6.6 {DOC 6.6PAMA.pdf Anexos VI: Comparison of Human Health Risks Associated with Lead, Arsenic, Cadmium, and SO2 in La Oroya Antigua--} </p> <p></ul></p> ','', '', NOW(), 1),
              (1109,1109,1,'PAMA 2004 - 2011', '<p>{DOC 0.1PAMA.pdf Fundamentos Para La Modificación Del Pama Del Complejo Metalurgico de La Oroya--} </p> <p>{DOC 0.2PAMA.pdf Indice--} </p> <p>1. {DOC 1PAMA.pdf Resumen Ejecutivo--} </p> <p>2. {DOC 2PAMA.pdf Antecedentes--} </p> <p>3. {DOC 3PAMA.pdf Compromiso PAMA Actual--} </p> <p>4. {DOC 4PAMA.pdf Para el Complejo Metalurgico de La Oroya Periodo 2004-2011--} </p> <p>5. {DOC 5PAMA.pdf Proyectos Incluidos en el PAMA para el Complejo Metalurgico do La Oroya Periodo 2004-2011--} </p> <p> <ul></p> <p>5.6 {DOC 5.6PAMA.pdf Planta de Acido Sulfurico y Sistema de Acondicionamiento de Gases--} </p> <p>5.7 {DOC 5.7PAMA.pdf Reduccion de Emision de Polvo por Chimenea--} </p> <p>5.8 {DOC 5.8PAMA.pdf Reduccion de Emision de Polvo Generado por Emisiones Fugitivas--} </p> <p>5.9 {DOC 5.9PAMA.pdf Proyectos Amientales Complementarios--} </p> <p></ul> </p> <p>6. {DOC 6PAMA.pdf Anexos Indice--} </p> <p> <ul></p> <p>6.1 {DOC 6.1PAMA.pdf Anexo I: Cronograma de Inversiones 2004-2011--} </p> <p>6.2 {DOC 6.2PAMA.pdf Anexo II: Diagramas de Gantt de los proyectos considerados en el plan ambiental 2004-2011--} </p> <p>6.3 {DOC 6.3PAMA.pdf Anexo III: Expectativas para mejorar la calidad del aire con respeto al Plomo--} </p> <p>6.4 {DOC 6.4PAMAS.pdf Anexo IV: Contribuciones comparadas de emisiones de las chimeneas y fugitivas de La Oroya--} </p> <p>6.5 {DOC 6.5PAMA.pdf Anexo V: Análisis de los datos de monitoreo para La Oroya--} </p> <p>6.6 {DOC 6.6PAMA.pdf Anexo VI: Comparación de los riesgos a la salud humana asociados con Plomo, Arsénico, Cadmio, y Dióxido de Azufre en La Oroya Antigua--} </p> <p></ul></p> ', '', '', NOW(), 1);
