            (117,     117,     'book','en',    'Atlantic Cod',1,   1,      NOW(),   NOW(), 0,0,0,0,117,0),
            (1117,1117,'book','es',    'La Situación del Bacalao en el Atlántico',1,   1,      NOW(),   NOW(), 0,0,0,0,117,0);
