            (nid,            vid,            type,  language,title,             uid, status, created, changed, comment, promote, moderate, sticky, tnid, translate) VALUES
            (118,     118,     'book','en',    'The Chilean Sea Bass',1,   1,      NOW(),   NOW(), 0,0,0,0,118,0),
            (1118,1118,'book','es',    'Bacalao de Profundidad',1,   1,      NOW(),   NOW(), 0,0,0,0,118,0);
