INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (127,127,1,   'AIDA Annual Report', '<p> <br></p> <p></br></p> <p><a href="http://www.aida-americas.org/templates/aida/uploads/docs/BiannualReport06-07.pdf">2006-2007 Annual Report</a></p> <p> <br></p> <p></br> </p> <p><a href="http://www.aida-americas.org/templates/aida/uploads/docs/2005_Annual_Report.pdf">2005 Annual Report</a></p> <p> <br></p> <p></br> </p> <p><a href="http://www.aida-americas.org/templates/aida/uploads/docs/AIDA_Fiscal_Year_2003-2004_Annual_Report.pdf">2004 Annual Report</p> ','', '', NOW(), 1),
              (1127,1127,1,'', '<p> <br></p> <p></br></p> <p><a href="http://www.aida-americas.org/templates/aida/uploads/docs/INFORME_BIANUAL2006-2007.pdf">Informe Annual de AIDA, Año Fiscal 2006-2007</a> </p> <p> <br></p> <p></br></p> <p><a href="http://www.aida-americas.org/templates/aida/uploads/docs/Anual_esp_2005.pdf">Informe Annual de AIDA, Año Fiscal 2004-2005</a> </p> <p> <br></p> <p></br></p> <p><a href="http://www.aida-americas.org/templates/aida/uploads/docs/Anual_ESP.pdf">Informe Annual de AIDA, Año Fiscal 2003-2004</a></p> ', '', '', NOW(), 1);
