              (131,131,1,   'Reference Documents', '<p> <ul> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/CRITIQUEBLACKSMITH.pdf">Critique of the May 2008 Blacksmith Institute report on La Oroya</a> <li><a href="/?q=laoroya.studies">Studies on Impacts to Health</a></li> <li><a href="/?q=laoroya.enviroquality">Studies on Environmental Quality</a></li> <li><a href="/?q=laoroya.remediation">Obligations and Plans for Environmental and Health Protection</a></li> <li><a href="/?q=laoroya.chronologicalsum">Chronological Summaries</a></li> <li><a href="/?q=laoroya.isoinfo">ISO 14001 Information</a></li> <li><a href="/?q=laoroya.economic">Economic Issues</a></li> <li><a href="/?q=laoroya.courtdecisions">Court Decisions and Cases</a></li> <li><a href="/?q=laoroya.pressreleases">Press Releases</a></li> <li><a href="/?q=laoroya.usefullinks">Useful Links!</a></li></ul></p> ','', '', NOW(), 1),
              (1131,1131,1,'Documentos Adicionales', '<p> <ul> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/CRITICABLACKSMITH.pdf">Crítica al Informe del Instituto Blacksmith sobre La Oroya, Mayo de 2008</a> <li><a href="/?q=laoroya.studies">Estudios de Impactos en la Salud</a></li> <li><a href="/?q=laoroya.enviroquality">Estudios de Calidad Ambiental</a></li> <li><a href="/?q=laoroya.remediation">Obligaciones y Planes para Protección Ambiental y de Salud</a></li> <li><a href="/?q=laoroya.chronologicalsum">Resumen Cronológico de los Eventos Relacionados con La Oroya</a></li> <li><a href="/?q=laoroya.isoinfo">Certifican con ISO 14001 a DRP</a></li> <li><a href="/?q=laoroya.economic">Asuntos Económicos</a></li> <li><a href="/?q=laoroya.courtdecisions">Decisiones de la Corte y Casos</a></li> <li><a href="/?q=laoroya.pressreleases">Comunicados de Prensa</a></li> <li><a href="/?q=laoroya.usefullinks">Enlaces Utiles!</a></li></ul></p> ', '', '', NOW(), 1);
