INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (132,132,1,   'Press Releases', '<p><center><a href="/?q=lo_subscribe"><b> <FONT COLOR="#ff8000"> Sign up now for La Oroya News!</FONT></b></a></center> </p> <p> <ol> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/LO_DOE_RUN_english.pdf"><b>Doe Run Smelter in La Oroya, Peru, Loses Environmental Certification </b> (April 9, 2008)</a> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/Press_La_Oroya_IACHR_CALL_URGENT_MEASURES.doc"><b>Human Rights Body Calls on Peru to Protect Citizens from Contamination by American-owned Smelter!</b> (September 5, 2007)</a> <li>{DOC IACHR_PETITION_TO_STATE_07-04-24.doc <b>IACHR Forwards La Oroya Human Rights Petition to Peruvian Government</b> for Comment} <li>{DOC Press_La_Oroya07-03-21.doc <b>AIDA and participating organizations submit La Oroya petition to the Inter-American Commission on Human Rights (March 21, 2007)</b>} <li><a href="/?q=laoroya.legalvictory0606"><b>Legal victory!</b> - Peruvian Constitutional Tribunal orders the government to address the public health emergency in La Oroya</a> <b>(June 2006)</b> <li><a href="/?q=laoroya.iachr"><b>Inter-American Commission on Human Rights</b> asked to Prevent a Public Health Crisis in La Oroya, Peru</a> <b>(November 2005)</b> <li><a href="/?q=laoroya.april05pr"><b>Judge Orders</b> Measures to Protect Public Health in La Oroya</a> <b>(April 2005)</b> <li>{DOC Plomo.pdf <b>Health risks in La Oroya are higher than expected</b>: Lead and lead compounds classified as carcinogens (Spanish text)} <b>(February 2004)</b> </p> <p></ol> </p> <p><center><a href="/?q=subscribe"><b> <FONT COLOR="#ff8000"> Sign up now for AIDA Monthly Updates</b></a> and stay informed!</FONT></center> </p> <p><center><a href="/?q=laoroya"><b>Read more about La Oroya!</b></a></center></p> ','', '', NOW(), 1),
              (1132,1132,1,'Comunicados de Prensa', '<p> <ol> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/LO_DOE_RUN_spanish.pdf"><b>El Complejo Metalúrgico Doe Run En La Oroya, Perú, Pierde Su Certificado Ambiental </b> (Abril 9, 2008)</a> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/LA_OROYA_CIDH_OTORGA_MEDIDAS.doc">Comisión Interamericana de Derechos Humanos (CIDH) <b>solicita al Estado Peruano implementar Medidas Cautelares de Urgencia para Proteger Vida de Pobladores de La Oroya Víctimas de la Contaminación!</b> (Septiembre 5, 2007)<a> <li>{DOC CIDH_ENVIA_INFO_AL_EDO07-04-24.doc <b>CIDH Remite Petición de Derechos Humanos de La Oroya</b> al estado Peruano para Comentarios} <li>{DOC NOTA_DE_PRENSA07-03-21.doc <b>AIDA y organizaciones participantes llevan el caso de La Oroya a la Comisión Interamericana de Derechos Humanos 07-03-21</b>} <li><a href="/?q=laoroya.legalvictory0606"><b>Victoria legal!</b> - El Tribunal Constitucional Peruano ordena al Gobierno atender la emergencia de salud pública en La Oroya</a> <b>(Junio 2006)</b> <li><a href="/?q=laoroya.iachr">Solicitan Medidas Cautelares ante <b>CIDH</b> para Enfrentar Crisis de Salud Publica en La Oroya, Peru</a> <b>(Noviembre 2005)</b> <li><a href="/?q=laoroya.april05pr"><b>Juez Ordena</b> Medidas para Recuperación de Salud Pública en La Oroya</a> <b>(Abril 2005)</b> <li>{DOC Plomo.pdf Riesgo de Salud en La Oroya es Mayor de lo Pensado: Plomo y Compuestos de Plomo Clasificados como Cancerígenos} <b>(Febrero 2004)</b> </p> <p></ol> </p> <p><center><a href="/?q=subscribe"><b><font color="99CC66">Suscríbase ahora a nuestras actualizaciones mensuales</font></b></a> y manténgase informado!</center> </p> <p><center><a href="/?q=laoroya"><b>Lea más acerca de La Oroya<b></a></center></p> ', '', '', NOW(), 1);
