            (nid,            vid,            type,  language,title,             uid, status, created, changed, comment, promote, moderate, sticky, tnid, translate) VALUES
            (134,     134,     'book','en',    'CICAD Interim Report & US Department of State Report to US Congress',1,   1,      NOW(),   NOW(), 0,0,0,0,134,0),
            (1134,1134,'book','es',    'Informe Provisional de la CICAD, Informe del Departamento de Estado al Congreso',1,   1,      NOW(),   NOW(), 0,0,0,0,134,0);
