INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (146,146,1,   'Government Documents', '<p>  <h5>COMMUNICATIONS WITH U.S. GOVERNMENT</h5> </br> </p> <p> <ol> <li><a href="http://www.state.gov/g/inl/rls/rpt/aeicc/c10854.htm">DoS 2003 Certification to the US Congress</a> <li><a href="/?q=plancolombia_conditionsnotmet2003">Conditions not met in 2003</a> <li>{DOC LETTER_TO_EPA.pdf Letter from Earthjustice to EPA (June 2003)--} <li>{DOC LETTER_TO_STATE.pdf Letter from Earthjustice to the Department of State (June 2003)--} <li><a href="http://www.earthjustice.org/urgent/documents/EPA_Letter_7-11-03.pdf">Reply from EPA (July 2003)</a> <li>{DOC RESPONSE_TO_EPA.pdf Earthjustice Response to EPA (October 2003)--} <li>{DOC REPLY_DOS.pdf Reply from the Department of State (August 2003)--} <li>{DOC RESPONSE_TO_STATE.pdf Earthjustice Response to the Department of State (October 2003)--} </p> <p></ol> </p> <p>Other Government Documents:</p> <p>{DOC LETTER03-09.pdf <b>Letter to State Council June 17, 2003</b> (Spanish only)}</p> ','', '', NOW(), 1),
              (1146,1146,1,'', '<p> <li><a href="http://usembassy.state.gov/colombia/wwwsea02.shtml">Certificación del Departamento de Estado al Congreso de los EEUU</a> <li><a href="/?q=plancolombia_conditionsnotmet2003">Condiciones incumplidas de 2003</a> <li>{DOC LETTER03-09.pdf <b>Carta al Consejo de Estado</b>} <li>{DOC 2002_Comments_on_DoS-SP.pdf Amenazas al Ambiente y a la Salud Humana por las Fumigaciones-Críticas a ls Certificación del Departamento de Estado presentada al Congreso de los EEUU en 2002--}</li>  <h4>COMUNICACIONES CON EL GOBIERNO DE LOS EE.UU. (en inglés)</h4>  <ol> <li>{DOC LETTER_TO_EPA.pdf Letter from Earthjustice to EPA (June 2003)--} <li>{DOC LETTER_TO_STATE.pdf Letter from Earthjustice to the Department of State (June 2003)--} <li><a href="http://www.earthjustice.org/urgent/documents/EPA_Letter_7-11-03.pdf">Reply from EPA (July 2003)</a> <li>{DOC RESPONSE_TO_EPA.pdf Earthjustice Response to EPA (October 2003)--} <li>{DOC REPLY_DOS.pdf Reply from the Department of State (August 2003)--} <li>{DOC RESPONSE_TO_STATE.pdf Earthjustice Response to the Department of State (October 2003)--} </p> <p></ol></p> ', '', '', NOW(), 1);
