INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (140,140,1,   'Studies on Impacts to Health', '<p> <ol> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/INFORME_UNIVERSIDAD_SAN_LUIS_MISSOURI.pdf"><b>Study on Environmental Contamination in Homes in the Cities of La Oroya and Concepción, and Health Impacts Among Residents,</b> University of St. Louis, Missouri, December, 2005 (Spanish Text)</a> </p> <p> <br> <li><b>CDC Report</b> - Development of an Integrated Intervention Plan to Reduce Exposure to Lead and Other Contaminants in the Mining Center of La Oroya, Perú <br /><i>(Prepared by: Centers for Disease Control and Prevention/Agency for Toxic Substances and Disease Registry, May 2005)</i> </p> <p> <ul> <li>{DOC CDC_Report__August_2005.pdf CDC Report on La Oroya, August 2005}</li> <li>{DOC CDC_Appendix_G.pdf CDC Report - Appendix G}</li> <li>{DOC CDC_Appendices_A_F_H_L.pdf CDC Report - Appendicies: A - F & H - L (Appendix B is in Spanish)}</li></ul> </p> <p> <br> <li><b><a href="/?q=laoroya.health_statistics">Blood Lead Study</b> of a Selected Population in La Oroya, DIGESA, November 1999</a> </p> <p></br> </p> <p> <br> <li><a href="http://www.slu.edu/Documents/university/KnowledgeWinter07.pdf"><b>Research Meets Mission in Peru, </b> Saint Louis University School of Public Health researchers help clean up the air in La Oroya, Peru, Winter 2007</a> </p> <p></br> </p> <p> <br> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/Peru_Serrano_1.pdf"><b>Environmental contamination in La Oroya, Peru, and its health effects in community residents</b></a> </p> <p></ol></p> ','', '', NOW(), 1),
              (1140,1140,1,'Estudios de Impactos en la Salud', '<p> <ol> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/INFORME_UNIVERSIDAD_SAN_LUIS_MISSOURI.pdf"><b> Estudio Sobre la Contaminación Ambiental en los Hogares de La Oroya y Concepción y sus Efectos en la Salud de sus Residentes,</b> Universidad de San Luis, Missouri, Diciembre 2005</a> </p> <p> <br> <li><b>Informe de la CDC</b>: Desarrollo de un Plan de Intervención Integral para Reducir la Exposición al Plomo y otros Contaminantes en el Centro Minero de La Oroya, Perú. <br /><i>(Preparado por: los Centros de Control y Prevención de Enfermedades/Agencia para el Registro de Sustancias Tóxicas y, Agosto de 2005)</i> </p> <p> <ul> <li>{DOC Informe_CDC_La_Oroya.pdf Informe CDC La Oroya 2005}</li> <li>{DOC Anexo_H.pdf Anexo H: Tres Estudios de Caso de Fundiciones}</li> <li>{DOC Anexo_I.pdf Anexo I Quelación}</li> <br></p> <p><b>Anexos adicionales en inglés:</b> <li>{DOC CDC_Appendix_G.pdf Anexo G}</li> <li>{DOC CDC_Appendices_A_F_H_L.pdf Anexos A-F y H-L}</li></ul> </p> <p> <br> <li><b><a href="/?q=laoroya.health_statistics">Estudio de Plomo en Sangre</b> en una Poblacion Seleccionada de La Oroya, DIGESA, Noviembre de 1999</a> </p> <p></br> </p> <p> <br> <li><a href="http://www.slu.edu/Documents/university/KnowledgeWinter07.pdf"><b>Se implementa investigación en Perú</b> investigadores de la Escuela de Salud Pública de la Universidad de Saint Louis, en los Estados Unidos, ayudan a limpiar el aire en La Oroya, Perú, Invierno 2007 (solamente en inglés) </p> <p></a> </p> <p></br> </p> <p> <br> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/Peru_Serrano_1.pdf"><b>Contaminación ambiental en La Oroya, Perú, y sus efectos sobre la salud de los oroínos</b></a> </p> <p></ol></p> ', '', '', NOW(), 1);
