              (145,145,1,   'Court Decisions and Cases', '<p> <ol> <li>{DOC MEDIDAS_CAUTELARES_07-09-04.pdf Human Rights Body Calls on Peru to Protect Citizens from Contamination by American-owned Smelter. September, 2007 (Spanish Text)} <li>{DOC LA_OROYA_Petition_DICIEMBRE_2006.pdf <b>Petition for case on violation of Human Rights before the IACHR</b>, December, 2006 (<b>Spanish Text</b>)} <li>{DOC Sentencia_Tribunal_Constitucional_LaOroya_MAY06.pdf <b>Constitutional Tribunal Ruling</b>, June 2006 (<b>Spanish Text</b>)} <li>{DOC Medidas_Cautelares-La_Oroya-Pub.pdf <b>Petition of precautionary measures to the Interamerican Commission on Human Rights</b> on behalf of inhabitants from La Oroya (<b>Spanish Text</b>)} <li>{DOC Ex-summary-precaut-La-Oroya-ENG.pdf <b>Executive Summary of the submission to IACHR</b>.} <li>{DOC MC-271-05_Respuesta-Pub.pdf <b>Reply to the Peruvian State response</b> of the submission to IACHR (<b>Spanish Text</b>)} <li>{DOC Resolucion_Juez_Civi_2005.pdf <b>Civil Court Ruling</b>, April 2005 (<b>Spanish Text</b>)} <li>{DOC Cumplimiento_MINSA_2002.pdf <b>Compliance Action filed against the Ministry of Health</b>, October 2002 (<b>Spanish Text</b>)} </p> <p></ol></p> ','', '', NOW(), 1),
              (1145,1145,1,'Decisiones de la Corte y Casos', '<p> <ol> <li>{DOC MEDIDAS_CAUTELARES_07-09-04.pdf Comisión Interamericana de Derechos Humanos (CIDH) solicita al Estado Peruano implementar Medidas Cautelares de Urgencia para Proteger Vida de Pobladores de La Oroya Víctimas de la Contaminación. Septiembre, 2007} <li>{DOC LA_OROYA_Petition_DICIEMBRE_2006.pdf <b>Petición de caso ante la CIDH por violaciones de Derechos Humanos</b>, Diciembre, 2006} <li>{DOC Sentencia_Tribunal_Constitucional_LaOroya_MAY06.pdf <b>Sentencia Tribunal Constitucional</b>, Junio de 2006} <li>{DOC Medidas_Cautelares-La_Oroya-Pub.pdf <b>Petición de medidas cautelares ante la Comisión Interamericana de Derechos Humanos</b> en nombre de los habitantes de La Oroya} <li>{DOC Ex-summary-precaut-La-Oroya-ENG.pdf <b>Resumen Ejecutivo de la solicitud ante la CIDH</b> (<b>Texto Inglés</b>)} <li>{DOC MC-271-05_Respuesta-Pub.pdf <b>Respuesta a la respuesta del Estado Peruano</b> ante la solicitud a la CIDH} <li>{DOC Resolucion_Juez_Civi_2005.pdf <b>Resolución Juez Civil</b>, Abril de 2005} <li>{DOC Cumplimiento_MINSA_2002.pdf <b>Accion de Cumplimiento Contra Ministerio de Salud</b>, Octubre de 2002} </p> <p></ol></p> ', '', '', NOW(), 1);
