              (147,147,1,   'Press Releases for Plan Colombia', '<p> <ol> <li>{DOC PRESS_RELEASE_07-02-12.pdf Plan Colombia Aerial Herbicide <b>Spraying Not Proved Safe</b> for the Environment – Released to US Congress <b>(February 14, 2007)</b>} <li><a href="/?q=plancolombia_ecuadorianborder">AIDA critical of Colombian <b>spraying in Ecuadorian border region</b></a> <b>(December 2006)</b> <li><a href="/?q=plancolombia_alternativespr"><b>AIDA Report Highlights Need for Greater Alternative Development Programs in Colombia</b></a> <b>(August 2006)</b> <li>{DOC Press_Release_12-03.pdf <b>US Congress Conditions: Spraying in National Parks</b>} <b>(December 2003)</b> <li>{DOC Press_Release_06-03.pdf <b>Colombian Court Orders the Suspension of Plan Colombia Spraying</b> AIDA press release on \'accion popular\'} <b>(June 2003)</b> </p> <p></ol> </p> <p><center><a href="/?q=subscribe"><b>Sign up now for AIDA monthly updates</b></a> and stay informed!</center></p> ','', '', NOW(), 1),
              (1147,1147,1,'Comunicados de Prensa', '<p> <ol> <li>{DOC PRESS_RELEASE_07-02-12.pdf Fumigación Aérea del Plan Colombia <b>NO HA PROBADO SER SEGURA</b> para el ambiente- Nota de Prensa para el Congreso de los Estados Unidos <b>(14 de Febrero de 2007)</b>} <li><a href="/?q=plancolombia_ecuadorianborder">AIDA cuestiona las fumigaciones de Colombia en la <b>frontera con Ecuador (Diciembre 2006)</b></a> <li><a href="/?q=plancolombia_alternativespr"><b>AIDA Evidencia Necesidad de Programas de Desarrollo Alternativo en Colombia (Agosto 2006)</b></a> <li>{DOC COMUNICADO_DE_PRENSA_FY2004.pdf Congreso de Estados Unidos Condiciona Fumigaciones en Parques Naturales de Colombia <b>(Diciembre 2003)</b>} <li>{DOC ACCION_POPULAR-SP.pdf <b>Tribunal Suspende Fumigaciones (Junio 2003)</b>}</li></ol> </p> <p><center><a href="/?q=subscribe"><b><font color="99CC66">Suscríbase ahora a nuestras actualizaciones mensuales</font></b></a> y manténgase informado!</center></p> ', '', '', NOW(), 1);
