INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (153,153,1,   'Legal Violations and Court Cases', '<p><b>Legal Violations</b> </p> <p> <ol> <li>Reports by Colombian General Accounting Office (Contraloría) <br /> <ul> <li>{DOC 5th_Report.pdf 5th Plan Colombia Evaluation Report, December 2004 (Spanish)} <li>{DOC 4th_Report.pdf 4th Plan Colombia Evaluation Report, July 2003 (Spanish)} <li>{DOC 3rd_Report.pdf 3rd Plan Colombia Evaluation Report, August 2002 (Spanish)}</li></ul> <li>Information from Colombian Public Defender’s Office (Defensoría del Pueblo) <br /> <ul> <li>{DOC Resolution_28.pdf The Coffee Crisis and Possible Spraying in Caldas, May, 2003 (Spanish)} <li>Summary of Public Defender’s concerns, September 2002 <li>{DOC Exec_Summary_Ombudsman.pdf Position on the Eradication of Illicit Crops, July 2002 - Summarized and translated by AIDA in collaboration with the Public Defender’s Office} <li>{DOC Resolution_26.pdf Human Rights and International Humanitarian Rights in Putumayo, October, 2002 (Spanish)} <li>{DOC Resolution_4.pdf Impact of the Spraying on 11 Alternative Development Projects, February, 2001, (Spanish)}</li></ul> <li>{DOC Violations_of_EMP_AIDA6-03.pdf Violations of the Environmental Management Plan for the Spraying Program, AIDA, June, 2003 (Spanish)} <li>{DOC FUMIGANDO_LA_DEMOCRACIA.pdf Eradicating Democracy, AIDA, June, 2004 (Spanish)} </p> <p></ol> </p> <p><b>Colombian Court Cases and Decisions</b> </p> <p> <ol> <li>{DOC AIDA_Demanda_Res.13_2003.pdf Challenge to Resolution Authorizing Spraying in National Parks, September, 2004 (Spanish)} <li>{DOC SAP_Consejo_Estado.pdf Ruling in suit alleging that the aerial spraying violates the right to a healthy environment, October 2004 (Spanish)} <li>{DOC Carta_al_Consejo_Estado.pdf Letter to the Colombian State Council regarding the Appeal of the Court Case on Aerial Eradication, June 9, 2003 (Spanish)} <li>{DOC SAP-Tribunal_Cundinamarca2003.pdf Ruling ordering the suspension of spraying until the government complies with the Environmental Management Plan, June, 2003} <li>{DOC Scia_Consejo_Estado2002.pdf State Council decision on the Environmental Management Plan, October, 2002 (Spanish)}</ol> </p> <p><b>Ecuadorian Court Cases</b> </p> <p> <ol> <li>{DOC Admin_Tribunal_Decision3-04.pdf Ecuadorian court decision about damages from spraying in the border region, March, 2004 (Spanish)}</ol></p> ','', '', NOW(), 1),
              (1153,1153,1,'Violaciones Legales y Jurisprudencia', '<p><b>Violaciones Legales</b> </p> <p> <ol> <li>Informes de la Contraloría General de la Nación de Colombia <br /> <ul> <li>{DOC 5th_Report.pdf Plan Colombia: Quinto Informe de Evaluación, Diciembre de 2004} <li>{DOC 4th_Report.pdf Plan Colombia: Cuarto Informe de Evaluación, Julio de 2003} <li>{DOC 3rd_Report.pdf Plan Colombia: Cuarto Informe de Evaluación, Agosto 2002}</li></ul> <li>Información de la Defensoría del Pueblo de Colombia <br /> <ul> <li>{DOC Resolution_28.pdf La Crisis Cafetera y Posibles Fumigaciones en el Departamento de Caldas, Mayo 2003} <li>Resumen de las Preocupaciones de la Defensoría del Pueblo, Septiembre 2002 <li>{DOC Exec_Summary_Ombudsman.pdf Posición de la Defensoría del Pueblo en Relación con la Erradicación de Cultivos Ilícitos, julio 2002- Resumido y Traducido por AIDA en Colaboración con la Defensoría del Pueblo (Inglés)} <li>{DOC Resolution_26.pdf Derechos Humanos y Derecho Internacional Humanitario en el Marco del Conflicto Armado y de las Fumigaciones de los Cultivos de Coca en el Departamento del Putumayo, 2002} <li>{DOC Resolution_4.pdf Impacto de las Fumigaciones en 11 Proyectos de Desarrollo Alternativo en el Putumayo, 2001}</li></ul> <li>{DOC Violations_of_EMP_AIDA6-03.pdf Violaciones al Plan de Manejo Ambiental para el Programa de Fumigaciones, AIDA, Junio de 2003} <li>{DOC FUMIGANDO_LA_DEMOCRACIA.pdf Fumigando la Democracia, AIDA, Junio, 2004} </p> <p></ol> </p> <p><b>Casos y Decisiones de las Cortes Colombianas</b> </p> <p> <ol> <li>{DOC AIDA_Demanda_Res.13_2003.pdf Demanda a la Resolución que Autoriza la Fumigación en Parques Nacionales, Septiembre 2004} <li>{DOC SAP_Consejo_Estado.pdf Fallo del Consejo de Estado de demanda alegando que las Fumigaciones Aéreas Violan el Derecho a un Medio Ambiente Sano, Octubre 2004} <li>{DOC Carta_al_Consejo_Estado.pdf Carta al Consejo de Estado Colombiano Relacionada con la Apelación al Caso de Fumigación Aérea, 9 de Junio de 2003} <li>{DOC SAP-Tribunal_Cundinamarca2003.pdf Fallo que Ordena la Suspensión de las Fumigaciones hasta que el Gobierno Cumpla con el Plan de Manejo Ambiental, Junio 2003} <li>{DOC Scia_Consejo_Estado2002.pdf Decisión del Consejo de Estado sobre el Plan de Manejo Ambiental, Octubre 2002}</ol> </p> <p><b>Casos de las Cortes Ecuatorianas</b> </p> <p> <ol> <li>{DOC Admin_Tribunal_Decision3-04.pdf Decisión del Tribunal Constitucional Ecuatoriano sobre los Daños por Fumigaciones en la Región Fronteriza, Marzo 2004}</ol></p> ', '', '', NOW(), 1);
