INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (163,163,1,   'Chronological Summaries', '<p>On a regular basis, AIDA summarizes local, regional, national, and international news associated with the La Oroya struggle. </p> <p> <ul> <li>{DOC Resumen_La_Oroya_ABRIL_MAYO_2007.pdf <b>April - May 2007</b> <i>(Spanish)</i>} <li>{DOC Resumen_La_Oroya_ENERO_MARZO_2007.pdf <b>January - March 2007</b> <i>(Spanish)</i>} <li>{DOC Resumen_La_Oroya_Agosto_2006.pdf <b>August 2006</b> <i>(Spanish)</i>}</li> <li>{DOC Resumen_La_Oroya_Enero_a_Julio_2006.pdf <b>January – July 2006</b> <i>(Spanish)</i>}</li></ul></p> ','', '', NOW(), 1),
              (1163,1163,1,'Resumen Cronológico', '<p>Regularmente AIDA resume noticias locales, regionales e internacionales asociadas con el conflicto en La Oroya. </p> <p> <ul> <li>{DOC Resumen_La_Oroya_ABRIL_MAYO_2007.pdf <b>Abril y Mayo, 2007</b>} <li>{DOC Resumen_La_Oroya_ENERO_MARZO_2007.pdf <b>Enero, Febrero y Marzo, 2007</b>} <li>{DOC Resumen_La_Oroya_Agosto_2006.pdf <b>Agosto 2006</b>}</li> <li>{DOC Resumen_La_Oroya_Enero_a_Julio_2006.pdf <b>Enero a Julio 2006</b>}</li></ul></p> ', '', '', NOW(), 1);
