INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (165,165,1,   'Information related to DRP compliance with 2006 PAMA', '<p>AIDA will be closely monitoring every aspect of Doe Run\'s compliance with the new PAMA obligations to make sure critical environmental improvements are not yet again delayed by the company, and that the government is enforcing relevant laws. </p> <p>Communications regarding PAMA compliance: </p> <p> <ol> <li>{DOC Carta_Cumplimiento_PAMA_06-08-01.pdf AIDA letter to MEM regarding Compliance, August 1, 2006 (Spanish)} <li>{DOC Respuesta_MEM_cumplimiento_06-08-14.pdf MEM Response August 14, 2006 (Spanish)} </p> <p></ol></p> ','', '', NOW(), 1),
              (1165,1165,1,'Información sobre el cumplimiento de DRP con el PAMA del 2006', '<p>AIDA va a monitorear muy de cerca cada aspecto relacionado con el cumplimiento de las obligaciones de Doe Run establecidas en el nuevo PAMA, para asegurarse que las mejoras ambientales críticas no se retrasen nuevamente por la compañía y que el gobierno ejecuta sus funciones. </p> <p>Comunicaciones sobre el estado de cumplimiento del PAMA: </p> <p> <ol> <li>{DOC Carta_Cumplimiento_PAMA_06-08-01.pdf Carta de AIDA al MEM relacionada con Cumplimiento, 1 de agosto de 2006} <li>{DOC Respuesta_MEM_cumplimiento_06-08-14.pdf Respuesta del MEM, 14 de agosto de 2006} </p> <p></ol></p> ', '', '', NOW(), 1);
