INSERT INTO node_revisions
              (nid,       vid,       uid, title,              body,             teaser,          log, timestamp,format) VALUES
              (170,170,1,   'Marine Protection Press Releases', '<p> <br></p> <p><b>Leatherback Turtles</b> </p> <p> <ul></p> <p> <ol> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/UNCONSTITUTIONAL_RULING.pdf">Costa Rican Court Reinforces Protection of Leatherback National Marine Park: Declares Ruling Allowing Construction in Park Unconstitutional <b> (June 3, 2008)</b></a> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/LEATHERBACK_PARK_PROTECTED_en.pdf">Costa Rican Court Orders Expropriation Of Land Slated For Tourism Development In The Leatherback Marine National Park <b> (May 14, 2008)</b></a></li> <li><a href="http://aida-americas.org/templates/aida/uploads/docs/Admision_Baulas_ENG.pdf">Legal Action Temporarily Protects Leatherback Sea Turtles in Costa Rica <b>(March 30, 2005)</b></a></li></ul> </ol></p> ','', '', NOW(), 1),
              (1170,1170,1,'Protección de Recursos Marinos-Comunicados de Prensa', '<p> <br></p> <p><b>Tortugas Baula</b> </p> <p> <ul></p> <p> <ol> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/REGLAMENTO_INCONSTITUCIONAL.pdf">Sala Constitucional Reitera Protección Al Parque Marino Las Baulas: Declara Inconstitucional Reglamento Que Permitía Construcciones <b>(3 de junio de 2008)</b></a></li> <li><a href="http://www.aida-americas.org/templates/aida/uploads/docs/Parque_las_Baulas_Protegido_sp.pdf">Sala Constitucional De Costa Rica Ordena Expropiaciones En Parque Nacional Marino Las Baulas<b> (14 de mayo de 2008)</b></a></li> <li><a href="http://aida-americas.org/templates/aida/uploads/docs/Admision_Baulas.pdf">Admitido Amparo para Proteccion de las Tortugas Baulas, Costa Rica <b>(30 de marzo de 2005)</b></a></li></ul> </ol></p> ', '', '', NOW(), 1);
