            (176,     176,     'book','en',    'Aquaculture Impacts in the Mediterranean',1,   1,      NOW(),   NOW(), 0,0,0,0,176,0),
            (1176,1176,'book','es',    'Impactos de la Acuicultura en el Mediterráneo',1,   1,      NOW(),   NOW(), 0,0,0,0,176,0);
